// Get the prompt from the previous node
const prompt = $('Create Prompt').first().json.prompt;

// Build the complete API request body
const requestBody = {
  model: "openai/gpt-4o-mini",
  messages: [
    {
      role: "user",
      content: prompt
    }
  ],
  temperature: 0.7,
  max_tokens: 2000
};

// Return the request body
return [{ json: { requestBody } }];
